   #include <linux/module.h>
   #include <linux/kernel.h>
   #include <linux/init.h>

   static int __init rootkit_init(void) {
       printk(KERN_INFO "Rootkit loaded\n");
       return 0;
   }

   static void __exit rootkit_exit(void) {
